import React from "react"
import "./header.css"

const Header = (account) => { 
    return (
        <header>
            {account? 
                <div className="btn btn-connect" onClick = {() => {window.ethereum.request({method: 'eth_requestAccounts'});}}>{account}</div>
                :
                <div className="btn btn-connect" onClick = {() => {window.ethereum.request({method: 'eth_requestAccounts'});}}>Please log in to metamask</div>
            }
        </header>
    )
}

export default Header;
