import web3 from './web3';
import './App.css';
import { useState, useEffect } from 'react';import Header from './components/header/Header';
import Footer from './components/footer/Footer';
import AttendBoard from './components/attend/AttendBoard';
import OwnerView from './components/owner/OwnerView';
import Gamble from './ultis/Gamble';

const App = () => {

  const [owner,setOwner] = useState('');
  const [winner,setWinner] = useState('');
  const [account,setAccount] = useState('');
  const [message, setMessage] = useState('');
  const [ended,setEnded] = useState('');

  useEffect(() => {
    async function asyncCalls() {
      await Gamble.methods.owner().call().then(result => setOwner(result));
      await web3.eth.getAccounts().then(result => setAccount(result));
      await Gamble.methods.ended().call().then(result => setEnded(result));
    }
    asyncCalls();
  }, [])
  console.log(ended)
  const onAttend = async ({id}) => {
    const accounts = await web3.eth.getAccounts(); 
    setMessage('Waiting for the transaction to be mined...');
    await Gamble.methods.Attend[id]().send({
      from: accounts[0],
      value: 10,
    });
    setMessage('You have been entered into planning at $');
  }

  return (
    <div className='App' style={{height:'30rem'}}>
      {/* <div>Current balance:</div>

      <button>b</button>
      <button onClick = {() => {
        window.ethereum.request({method: 'eth_requestAccounts'});
      }}
      >Connect Metamask</button>
      <span>
        <p>Address</p>
        {account ? account : "log in please"}
        <p> end: {String(ended)}</p>
        <h2>{message}</h2>
        <p>{String(account) === String(owner) ? String(ended) : ""}</p> */}
      <Header account = {account}/>
      <div>Plan to Earn</div>
      {account[0] === owner ? 
        <OwnerView/>
        :
        <AttendBoard onAttend={onAttend}/>
      }

      {ended === true ? 
        <div style={{margin:'auto'}}>Winner: {winner}</div> 
        // withdraw buttons for winner
        :
        <div/>
      }

      <Footer/>
    </div>
  );
}

export default App;
