import React from "react"
import "./header.css"

const Header = ({account}) => {
    return (
        <header>
            {account[0] ?
                <h2>{account[0]}</h2>
                :
                <div className="btn btn-metamask" onClick = {() => {window.ethereum.request({method: 'eth_requestAccounts'});}}>Connect Metamask</div>
            }
        </header>
    )
}

export default Header;
