import React from "react"
import { useState } from "react"
import "./attendboard.css"

const AttendBoard = ({onAttend}) => { 
    const [value, setValue] = useState('')
    const onSubmit = (e) => {
        e.preventDefault()
        if(!value) {
            alert("Please enter deposit amount")
            return
        }
        else if (value < 10) {
            alert("Incorrect amount of payment (10 wei)")
            return
        }
        onAttend(value)
    }
    return (
        <div>
        <board> 
        <form onSubmit={onSubmit}>
                <div>
                    <input
                        className='form-control input' 
                        placeholder='0.0'
                        type="text"
                        value = {value} 
                        onChange = {(e) => setValue(e.target.value)}/>
                </div>
                <div style = {{height: '100%', textAlign: 'end'}}>
                    <input className='btn btn-deposit' type="submit" value='Deposit' style={{marginRight: '0'}}/>
                </div>
            </form>
        </board>
        </div>
    )
}

export default AttendBoard;
