import logo from './logo.svg';
import './App.css';
import Web3 from "web3";
import { useState, useEffect } from 'react';
import detectEthereumProvider from '@metamask/detect-provider';
import {Gamble} from './ultis/gamble';
import { LoadContract } from './ultis/load-contract';

const App = () => {
  const [web3Api, setWeb3Api] = useState({
    provider: null,
    web3: null,
    contract: null
  });

  const [account, setAccount] = useState(null);
  const [owner, setOwner] = useState('');
  const abi = [
    {
      "inputs": [
        {
          "internalType": "uint256",
          "name": "_slot",
          "type": "uint256"
        }
      ],
      "stateMutability": "nonpayable",
      "type": "constructor"
    },
    {
      "inputs": [],
      "name": "ended",
      "outputs": [
        {
          "internalType": "bool",
          "name": "",
          "type": "bool"
        }
      ],
      "stateMutability": "view",
      "type": "function",
      "constant": true
    },
    {
      "inputs": [],
      "name": "open",
      "outputs": [
        {
          "internalType": "bool",
          "name": "",
          "type": "bool"
        }
      ],
      "stateMutability": "view",
      "type": "function",
      "constant": true
    },
    {
      "inputs": [],
      "name": "owner",
      "outputs": [
        {
          "internalType": "address",
          "name": "",
          "type": "address"
        }
      ],
      "stateMutability": "view",
      "type": "function",
      "constant": true
    },
    {
      "inputs": [],
      "name": "winner",
      "outputs": [
        {
          "internalType": "uint256",
          "name": "",
          "type": "uint256"
        }
      ],
      "stateMutability": "view",
      "type": "function",
      "constant": true
    },
    {
      "inputs": [
        {
          "internalType": "uint256",
          "name": "team_id",
          "type": "uint256"
        }
      ],
      "name": "Attend",
      "outputs": [],
      "stateMutability": "payable",
      "type": "function",
      "payable": true
    },
    {
      "inputs": [],
      "name": "viewTeam",
      "outputs": [
        {
          "internalType": "uint256",
          "name": "",
          "type": "uint256"
        }
      ],
      "stateMutability": "view",
      "type": "function",
      "constant": true
    },
    {
      "inputs": [],
      "name": "OpenGame",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [],
      "name": "CloseGame",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [],
      "name": "EndGame",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [],
      "name": "WithdrawFund",
      "outputs": [],
      "stateMutability": "payable",
      "type": "function",
      "payable": true
    }
  ];
  useEffect( () => {
    const loadProvider = async () => {
      const provider= await detectEthereumProvider();
      const contract = new web3Api.web3.eth.Contract(
        abi,0x5e128B065F7fFa70F6544ddcA3DBc82868E7465d,
      );
      debugger
      if (provider) {
        setWeb3Api ({web3: new Web3(provider), provider, contract})
      } else {
        console.error("Install metamask")
      }
    }
    loadProvider()  
  },[]);
  useEffect(() => { 
    const getAccount = async () => {
      const accounts = await web3Api.web3.eth.getAccounts()
      setAccount(accounts[0])
    }
    web3Api.web3 && getAccount()
  },[web3Api.web3]);

  console.log(owner);
  return (
    <div>
      <div>Current balance: 10 eth</div>
      <button>a</button>
      <button>b</button>
      <button onClick = {() => {
        web3Api.provider.request({method: "eth_requestAccounts"})
      }}
      >Connect Metamask</button>
      <span>
        <p>Address</p>
        {account ? account : "Log in pls"}
      </span>
    </div>
  );
}

export default App;
