import logo from './logo.svg';
import './App.css';
import Web3 from "web3";
import { useState, useEffect } from 'react';
import detectEthereumProvider from '@metamask/detect-provider';
import Gamble from './ultis/Gamble';

const App = () => {
  const [web3Api, setWeb3Api] = useState({
    provider: null,
    web3: null,
    contract: null
  });

  const [account, setAccount] = useState(null);
  const [balance, setBalance] = useState(null);
  const [owner, setOwner] = useState(null);

  useEffect( () => {
    const loadProvider = async () => {
      const provider= await detectEthereumProvider();
      const contract = await Gamble;
      debugger
      if (provider) {
        setWeb3Api ({web3: new Web3(provider), provider, contract})
      } else {
        console.error("Install metamask")
      }
    }
    loadProvider()  
  },[]);
  useEffect(() => { 
    const getAccount = async () => {
      const accounts = await web3Api.web3.eth.getAccounts()
      setAccount(accounts[0])
    }
    web3Api.web3 && getAccount()
  },[web3Api.web3]);

  useEffect (() => {
    const balanceLoad = async () => {
      const  {contract, web3} = web3Api;
      const balance = await web3.eth.getBalance(contract.address);
      setBalance(balance,"ether")
    }
    web3Api.contract || balanceLoad()
  }, [web3Api])
  return (
    <div>
      <div>Current balance: {balance}</div>
      <button>a</button>
      <button>b</button>
      <button onClick = {() => {
        web3Api.provider.request({method: "eth_requestAccounts"})
      }}
      >Connect Metamask</button>
      <span>
        <p>Address</p>
        {account ? account : "Log in pls"}
      </span>
    </div>
  );
}

export default App;
