import React from "react"
import { useState } from "react"
import "./attendboard.css"

const AttendBoard = ({onAttend}) => {
    const [id, setId] = useState('')
    const onSubmit = (e) => {
        e.preventDefault()
        if(!id) {
            alert("Please enter id of the team")
            return
        } else if(id >2) {
            alert("Please enter valid id (from 0 to 3)")
            return
        }
        // onAttend(id)
        console.log(id)
    }
    return (
        <div>
        <board> 
        <form className="form" onSubmit={onSubmit}>
                    <input
                        className='input_name'
                        type="text"
                        value = {id}
                        placeholder = "Select team id"
                        onChange = {(e) => setId(e.target.value)}/>
                <div style = {{height: '100%', textAlign: 'end', display: 'flex', flexDirection: 'row', justifyContent: 'space-between'}}>
                    <div style={{fontSize: '30px'}}>1 TICKET = 10 WEI</div>
                    <input className='btn btn-secondary' type="submit" value='Deposit' style={{marginRight: '0'}}/>
                </div>
            </form>
        </board>
        </div>
    )
}

export default AttendBoard;
