import logo from './logo.svg';
import './App.css';
import Web3 from "web3";
import { useState, useEffect } from 'react';
import detectEthereumProvider from '@metamask/detect-provider';

const App = () => {
  const [web3Api, setWeb3Api] = useState({
    provider: null,
    web3: null,
    contract: null
  });

  const [account, setAccount] = useState(null);
  const [owner, setOwner] = useState('');
  const abi = [{"inputs":[],"stateMutability":"nonpayable","type":"constructor","signature":"constructor"},{"inputs":[],"name":"enter","outputs":[],"stateMutability":"payable","type":"function","payable":true,"signature":"0xe97dcb62"},{"inputs":[],"name":"getPlayers","outputs":[{"internalType":"address[]","name":"","type":"address[]"}],"stateMutability":"view","type":"function","constant":true,"signature":"0x8b5b9ccc"},{"inputs":[],"name":"getPrize","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function","constant":true,"signature":"0xc34f6b0d"},{"inputs":[],"name":"getUser","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function","constant":true,"signature":"0x832880e7"},{"inputs":[],"name":"manager","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function","constant":true,"signature":"0x481c6a75"},{"inputs":[],"name":"pickWinner","outputs":[],"stateMutability":"nonpayable","type":"function","signature":"0x5d495aea"},{"inputs":[{"internalType":"uint256","name":"","type":"uint256"}],"name":"players","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function","constant":true,"signature":"0xf71d96cb"}];

  useEffect( () => {
    const loadProvider = async () => {
      const provider= await detectEthereumProvider();
      const contract = await new web3Api.web3.eth.Contract(
        abi,0x617D333F02F27e5cFd37ae445588CEf13588b265,
      );
      debugger
      if (provider) {
        setWeb3Api ({web3: new Web3(provider), provider, contract})
      } else {
        console.error("Install metamask")
      }
    }
    loadProvider()  
  },[]);
  useEffect(() => { 
    const getAccount = async () => {
      const accounts = await web3Api.web3.eth.getAccounts()
      setAccount(accounts[0])
    }
    web3Api.web3 && getAccount()
  },[web3Api.web3]);

  console.log(owner);
  return (
    <div>
      <div>Current balance: 10 eth</div>
      <button>a</button>
      <button>b</button>
      <button onClick = {() => {
        web3Api.provider.request({method: "eth_requestAccounts"})
      }}
      >Connect Metamask</button>
      <span>
        <p>Address</p>
        {account ? account : "Log in pls"}
      </span>
    </div>
  );
}

export default App;
